import React from 'react';
import './App.css';
import {Redirect, Route, Switch} from "react-router-dom";
import LogIn from "./components/login";
import ChatPage from "./components/chatPage";
import {useSelector} from "react-redux";
import {accountSelector} from "./redux/Account";


function PrivateRoute({ children, ...rest }) {
  const accountState = useSelector(accountSelector.getAccountState)

  return (
    <Route
      {...rest}
      render={() => {
        return accountState.data.isAuthorized ? (children) : (<Redirect to="/login" exact/>);
      }}
    />
  );
}

function App() {
  const accountState = useSelector(accountSelector.getAccountState)

  const defaultRedirect = !accountState.data.isAuthorized ? <Redirect to={'/chat'} exact/> :
    <Redirect from={'/*'} to={'/login'} exact/>

  return (
    <div className="App">
      <div>
          <Switch>
            <Route exact path='/login'>
              <LogIn/>
            </Route>
            <PrivateRoute exact path='/chat'>
              <ChatPage/>
            </PrivateRoute>
            {defaultRedirect}
          </Switch>
      </div>
    </div>
  );
}

export default App;
